import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import './App.css';
import CharacterCard from './CharacterCard';
import characters from './protagonists.json'




var requestOptions = {
  method: 'GET',
  redirect: 'follow'
};


function App() {

  let animalData = "";  
  console.log("before fetch");

  fetch("https://your-url/stuff", requestOptions)
  .then(response => response.text())
  .then(result => {
    console.log("after fetch");
    animalData = result;
    console.log("animals JSON data", result)
    animalData = result;
  })
  .catch(error => console.log('error', error));

  const [ SearchBar, setSearchBar ] = useState('');
  const [ menu_url, setMenu_url ] = useState(`https://www.themealdb.com/api/json/v1/1/filter.php?i=${SearchBar}`)
  const [ menu, setMenu ] = useState('');
  const [ searchResult, setSearchResult ] = useState(null);
  const [ menuData, setMenuData ] = useState([]);


  const displayMenu = async () => {
    try {
      //before fetch
      const MENU_URL = `https://www.themealdb.com/api/json/v1/1/filter.php?i=${SearchBar}`
      const response = await fetch (`${MENU_URL}`, requestOptions);
      console.log("before response", response);
      const data = await response.json();
      // data[0].name

      console.log("display menu", data);
      setMenu(data.meals);
      //setMenu(data.meals[0].strMeal);

    } catch (e) {
      console.log("my error", e)
    }

  }

  useEffect(() => {
    displayMenu();
  }, [searchResult])
  //doesn't iterate until searchResult changes

  const searchIng = async ()=>{
    var requestOptions = {
      method: 'GET',
      redirect: 'follow'
    };

    //start fetch with Ingredients
    try {
      const response = await fetch (`${menu_url}`, requestOptions);
      const data = await response.json(); //2s
      setSearchResult(data)

    } catch (e) {
      console.log("my error from searchIng", e)
    }
    //useState can be accessed everywhere in component

    console.log("get api data", SearchBar)

    //give the input to api
    //get the api data
    //display the data
  }


  return (
    <div className="App">
      <CssBaseline />
      <AppBar
        position="static"
        color="default"
        elevation={0}
        sx={{ borderBottom: '1px solid lightgray' }}
      >
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Characters Inc
          </Typography>
          <Button 
            href="#" 
            variant="outlined" 
            sx={{ my: 1, mx: 1.5 }}
            onClick={() => alert("Boop!")}
          >
            Button
          </Button>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ my: 4}}>
        <Typography
          variant="h2"
          align="center"
          color="text.primary"
          sx={{ py: 2}}
        >
          Prevalent Protagonists
        </Typography>
        <Typography 
          variant="h5" 
          align="center" 
          color="text.secondary"
          sx={{ mx: 10 }}
        >
          Hmm, seems like we're missing some of the other protagonists.
        </Typography>
      </Container>
      {/* End hero unit */}

      <Container maxWidth="lg">
        <Grid container 
          spacing={5} 
          justifyContent="center"
          alignItems="flex-start"
        >
          {characters.map((data) =>
          <Grid
          item
          xs={12}
          md={4}
          >
          <CharacterCard
            characterName={data.title}
            image={data.pic}
            descriptionArray={data.description}
          />
        </Grid>
        )}
        </Grid>




      </Container>
    </div>
  );
}

export default App;
